import React from "react";
import "./styles.css"

function List({setToDo,list}) {
   
    const toogle = (index) => {
        
        let newArr = [...list]

        list[index].complate = list[index].complate

        setToDo(list)

        console.log(list)
    }

    const destroy = (index) => {

        setToDo(
            list.filter((todo,id)=>{
                if(id != index)
                {
                    return todo;
                }
            }))
    }

    return (
        
        <section className="main">

            <ul className="todo-list">
                {
                    list.map((todo,index)=>(
                            
                    <li key={index} className={todo.complate ? "completed": null }>
                        {console.log(todo.complate)}
                        <div className="view">
                            <input className="toggle" type="checkbox" onClick={()=> toogle(index)} ></input>
                            <label>{todo.value}</label>
                            <button className="destroy" onClick={()=> destroy(index)}></button>
                        </div>
                    </li>
                    
                    ))
                }
            </ul>

        </section>

    );
}

export default List
