import React,{useState} from 'react'
import "./styles.css"

function AddToDo({setToDo,list}) {
    
    const [text, addText] = useState("");

    const onSubmit = (event) => {

        event.preventDefault();

        if(text != "")
        {
            setToDo([...list,text])
        }

        event.target.value = " aennasdasd"
    }

    return (
        <form onSubmit={onSubmit}>
            <input onChange={(event)=>addText(event.target.value)} className="new-todo" placeholder='What needs to be done'></input>
        </form>
    )
}

export default AddToDo
