import "./styles.css"

function List({setToDo,list}) {
   

    const destroy = (id) => {
        
        const newList = list.filter((todo,id)=>{
            if(id != index)
            {
                return todo;
            };
        })

        setToDo(newList)

    }
    

    return (
        <ul className="todo-list">
            {
                list.map((todo,index)=>(
                                
                <li key={index}>

                    <div className="view">
                        <input className="toggle" checked = {todo.complate} type="checkbox"></input>
                        <label>{todo.value}</label>
                        <button className="destroy" onClick={(index) => destroy(index)} ></button>
                    </div>
                </li>
                
                ))
            }
        </ul>
    )
}

export default List
