import React from 'react'
import "./styles.css"

function List({setToDo,list}) {
   

    const toogle = (index) => {

        const listTemp = list; 

        setToDo(
            list.filter((todo,id)=>{
                if(id != index)
                {
                    return todo;
                }
            }))
    }

    const destroy = (index) => {

        setToDo(
            list.filter((todo,id)=>{
                if(id != index)
                {
                    return todo;
                }
            }))
    }

    return (
        <ul className="todo-list">
            {
                list.map((todo,index)=>(
                                
                <li key={index}>

                    <div className="view">
                        <input className="toggle" checked = {todo.complate} type="checkbox"></input>
                        <label>{todo.value}</label>
                        <button className="destroy" onClick={()=> destroy(index)}></button>
                    </div>
                </li>
                
                ))
            }
        </ul>
    )
}

export default List
