import React from "react";
import "./styles.css"

function List({ setToDo, list }) {

    const toogle = (index) => {

        console.log(index)
    }

    const destroy = (index) => {

        setToDo(
            list.filter((todo, id) => {
                if (id != index) {
                    return todo;
                }
            }))
    }

    return (

        <section className="main">

            <ul className="todo-list">
                {
                    list.map((todo, index) => (

                        <li key={index}>

                            <div className="view">
                                <div>
                                    <input className="toggle" type="checkbox" onClick={() => toogle(index)} ></input>
                                    <label>{todo.value}</label>
                                </div>
                                <button className="destroy" onClick={() => destroy(index)}></button>
                            </div>
                        </li>

                    ))
                }
            </ul>

        </section>

    );
}

export default List
