import React from 'react'
import "./styles.css"

function List({setToDo,list}) {
   
    return (
        <ul className="todo-list">
            {
                list.map((text,index)=>(
                                
                <li key={index}>

                    <div className="view">
                        <input className="toggle" type="checkbox"></input>
                        <label>{text}</label>
                        <button className="destroy" onClick={()=> setToDo(
                                list.filter((value,id)=>{
                                    if(id === index)
                                    {
                                        return value;
                                    }
                                })
                        )}></button>
                    </div>
                </li>
                
                ))
            }
        </ul>
    )
}

export default List
